class Solution:
    def maxRunTime(self, n: int, batteries: List[int]) -> int:
        left, right = 1, sum(batteries) // n

        batteries.sort()
        selected = batteries[-n:]
        total_extra = sum(batteries[:-n]) if len(batteries) > n else 0

        def is_possible(time):
