class Solution:
    def minimumDiameterAfterMerge(self, edges1: List[List[int]], edges2: List[List[int]]) -> int:
        def build_tree(edges):
            tree = {}
            for u, v in edges:
                if u not in tree:
                    tree[u] = []
                if v not in tree:
                    tree[v] = []
                tree[u].append(v)
                tree[v].append(u)
            return tree

        def get_tree_properties(tree):
            if not tree:
                return (0, 0, [])

            def bfs(start):
                visited = {start: 0}
                q = deque([start])
                farthest_node = start
                max_dist = 0
                while q:
                    node = q.popleft()
                    for neighbor in tree.get(node, []):
                        if neighbor not in visited:
                            visited[neighbor] = visited[node] + 1
                            q.append(neighbor)
                            if visited[neighbor] > max_dist:
                                max_dist = visited[neighbor]
                                farthest_node = neighbor
                return farthest_node, max_dist

            node, _ = bfs(next(iter(tree.keys())))
            node, diameter = bfs(node)

            def find_centers():
                if diameter == 0:
                    return [node]
                leaves = deque()
                degree = {}
                for node in tree:
                    degree[node] = len(tree[node])
                    if degree[node] == 1:
                        leaves.append(node)
                total_nodes = len(tree)
                while total_nodes > 2:
                    level_size = len(leaves)
                    for _ in range(level_size):
                        leaf = leaves.popleft()
                        for neighbor in tree[leaf]:
                            if degree[neighbor] > 1:
                                degree[neighbor] -= 1
                                if degree[neighbor] == 1:
                                    leaves.append(neighbor)
                        total_nodes -= 1
                return list(leaves)

            center_nodes = find_centers()
            radius = (diameter + 1) // 2
            return (diameter, radius, center_nodes)

        tree1 = build_tree(edges1)
        tree2 = build_tree(edges2)

        d1, r1, centers1 = get_tree_properties(tree1)
        d2, r2, centers2 = get_tree_properties(tree2)

        min_diameter = max(d1, d2, r1 + r2 + 1)
