        max_num = 1000
        sieve = [True] * (max_num + 1)
        sieve[0] = sieve[1] = False
        for i in range(2, int(max_num ** 0.5) + 1):
            if sieve[i]:
                sieve[i*i : max_num+1 : i] = [False] * len(sieve[i*i : max_num+1 : i])

        primes = [i for i, is_p in enumerate(sieve) if is_p]

        n = len(nums)
        if n == 1:
            return True

        prev = 0
        for i in range(n):
            if nums[i] <= prev:
                return False
            target = nums[i] - prev - 1
            left, right = 0, len(primes) - 1
            best_p = 0
            while left <= right:
                mid = (left + right) // 2
                if primes[mid] <= target:
                    best_p = primes[mid]
                    left = mid + 1
                else:
                    right = mid - 1
            if best_p > 0:
                nums[i] -= best_p
            prev = nums[i]
