class Solution:
    def longestPalindromicSubsequence(self, s: str, k: int) -> int:
        n = len(s)
        memo = {}

        def dp(i, j, k_left):
            if (i, j, k_left) in memo:
                return memo[(i, j, k_left)]
            if i > j:
                return 0
            if i == j:
                return 1
            if s[i] == s[j]:
                res = 2 + dp(i + 1, j - 1, k_left)
            else:
                max_res = max(dp(i + 1, j, k_left), dp(i, j - 1, k_left))
                if k_left > 0:
                    cost_i = (ord(s[j]) - ord(s[i])) % 26
                    cost_i = min(cost_i, 26 - cost_i)
                    if cost_i <= k_left:
                        option1 = 2 + dp(i + 1, j - 1, k_left - cost_i)
                        max_res = max(max_res, option1)
                    cost_j = (ord(s[i]) - ord(s[j])) % 26
                    cost_j = min(cost_j, 26 - cost_j)
                    if cost_j <= k_left:
                        option2 = 2 + dp(i + 1, j - 1, k_left - cost_j)
                        max_res = max(max_res, option2)
                res = max_res
            memo[(i, j, k_left)] = res
            return res

        return dp(0, n - 1, k)
