class Solution:
    def totalStrength(self, strength: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(strength)

        prefix = [0] * (n + 1)
        for i in range(n):
            prefix[i+1] = (prefix[i] + strength[i]) % MOD

        prefix_prefix = [0] * (n + 2)
        for i in range(n + 1):
            prefix_prefix[i+1] = (prefix_prefix[i] + prefix[i]) % MOD

        left = [-1] * n
        stack = []
        for i in range(n):
            while stack and strength[stack[-1]] >= strength[i]:
                stack.pop()
            if stack:
                left[i] = stack[-1]
            else:
                left[i] = -1
            stack.append(i)

        right = [n] * n
        stack = []
        for i in range(n-1, -1, -1):
            while stack and strength[stack[-1]] > strength[i]:
                stack.pop()
            if stack:
                right[i] = stack[-1]
            else:
                right[i] = n
            stack.append(i)

        res = 0
        for i in range(n):
            l, r = left[i] + 1, right[i] - 1
            sum_left = (i - l + 1) * (prefix_prefix[r+2] - prefix_prefix[i+1]) % MOD
            sum_right = (r - i + 1) * (prefix_prefix[i+1] - prefix_prefix[l]) % MOD
            total = (sum_left - sum_right) % MOD
            res = (res + strength[i] * total) % MOD
        return res
