class Solution:
    def lengthAfterTransformations(self, s: str, t: int) -> int:
        MOD = 10**9 + 7
        count = [0] * 26
        for c in s:
            count[ord(c) - ord('a')] += 1





        M = [[0]*26 for _ in range(26)]
        for i in range(25):
            M[i][i+1] = 1
        M[25][0] = 1
        M[25][1] = 1

        def matrix_mult(a, b):
            result = [[0]*26 for _ in range(26)]
            for i in range(26):
                for k in range(26):
                    if a[i][k]:
                        for j in range(26):
                            result[i][j] = (result[i][j] + a[i][k] * b[k][j]) % MOD
            return result

        def matrix_pow(mat, power):
            result = [[0]*26 for _ in range(26)]
            for i in range(26):
                result[i][i] = 1

            while power > 0:
                if power % 2 == 1:
                    result = matrix_mult(result, mat)
                mat = matrix_mult(mat, mat)
                power //= 2
            return result

        if t > 0:
            mat_t = matrix_pow(M, t)
            new_count = [0] * 26
            for i in range(26):
                for j in range(26):
                    new_count[j] = (new_count[j] + count[i] * mat_t[i][j]) % MOD
            count = new_count

        return sum(count) % MOD
