class Solution:
    def shiftDistance(self, s: str, t: str, nextCost: List[int], previousCost: List[int]) -> int:
        total_cost = 0
        next_prefix = [0] * 26
        next_prefix[0] = nextCost[0]
        for i in range(1, 26):
            next_prefix[i] = next_prefix[i-1] + nextCost[i]

        prev_prefix = [0] * 26
        prev_prefix[0] = previousCost[0]
        for i in range(1, 26):
            prev_prefix[i] = prev_prefix[i-1] + previousCost[i]

        for sc, tc in zip(s, t):
            if sc == tc:
                continue
            sc_ord = ord(sc) - ord('a')
            tc_ord = ord(tc) - ord('a')

            forward_shifts = (tc_ord - sc_ord) % 26
            backward_shifts = (sc_ord - tc_ord) % 26

            if forward_shifts == 0:
                forward_cost = 0
            else:
                if sc_ord + forward_shifts <= 25:
                    forward_cost = next_prefix[sc_ord + forward_shifts - 1] - (next_prefix[sc_ord - 1] if sc_ord > 0 else 0)
                else:
                    forward_cost = (next_prefix[25] - (next_prefix[sc_ord - 1] if sc_ord > 0 else 0)) + next_prefix[(sc_ord + forward_shifts - 1) % 26]

            if backward_shifts == 0:
                backward_cost = 0
            else:
                if sc_ord - backward_shifts >= 0:
                    backward_cost = prev_prefix[sc_ord] - (prev_prefix[sc_ord - backward_shifts] if sc_ord - backward_shifts > 0 else 0)
                else:
                    backward_cost = (prev_prefix[sc_ord] - 0) + (prev_prefix[25] - prev_prefix[(sc_ord - backward_shifts) % 26 - 1] if (sc_ord - backward_shifts) % 26 > 0 else prev_prefix[25])

            total_cost += min(forward_cost, backward_cost)

