from typing import List

class Solution:
    def countCompleteComponents(self, n: int, edges: List[List[int]]) -> int:
        parent = [i for i in range(n)]
        rank = [1] * n

        def find(u):
            while parent[u] != u:
                parent[u] = parent[parent[u]]
                u = parent[u]
            return u

        def union(u, v):
            u_root = find(u)
            v_root = find(v)
            if u_root == v_root:
                return
            if rank[u_root] > rank[v_root]:
                parent[v_root] = u_root
                rank[u_root] += rank[v_root]
            else:
                parent[u_root] = v_root
                rank[v_root] += rank[u_root]

        for u, v in edges:
            union(u, v)

        components = {}
        edge_count = {}
        for u in range(n):
            root = find(u)
            if root not in components:
                components[root] = []
                edge_count[root] = 0
            components[root].append(u)

        for u, v in edges:
            root = find(u)
            edge_count[root] += 1

        count = 0
        for root in components:
            m = len(components[root])
            required_edges = m * (m - 1) // 2
            if edge_count[root] == required_edges:
                count += 1
        return count
