class Solution:
    def maximumSum(self, nums: List[int]) -> int:
        def get_square_free(x):
            res = 1
            if x % 2 == 0:
                cnt = 0
                while x % 2 == 0:
                    x //= 2
                    cnt += 1
                if cnt % 2 == 1:
                    res *= 2
            i = 3
            max_factor = math.isqrt(x)
            while i <= max_factor:
                if x % i == 0:
                    cnt = 0
                    while x % i == 0:
                        x //= i
                        cnt += 1
                    if cnt % 2 == 1:
                        res *= i
                    max_factor = math.isqrt(x)
                i += 2
            if x > 1:
                res *= x
            return res

        groups = defaultdict(int)
        for i in range(1, len(nums) + 1):
            sf = get_square_free(i)
            groups[sf] += nums[i - 1]

        return max(groups.values()) if groups else 0
