class Solution:
    def minimumDifference(self, nums: List[int]) -> int:
        n = len(nums) // 2
        total = sum(nums)
        target = total // 2

        left = nums[:n]
        right = nums[n:]

        left_sums = [[] for _ in range(n+1)]
        right_sums = [[] for _ in range(n+1)]

        for mask in range(1 << n):
            cnt = bin(mask).count('1')
            sum_l = 0
            for i in range(n):
                if mask & (1 << i):
                    sum_l += left[i]
            left_sums[cnt].append(sum_l)

        for mask in range(1 << n):
            cnt = bin(mask).count('1')
            sum_r = 0
            for i in range(n):
                if mask & (1 << i):
                    sum_r += right[i]
            right_sums[cnt].append(sum_r)

        for k in range(n+1):
            right_sums[k].sort()

        min_diff = float('inf')

        for k in range(n+1):
            left_arr = left_sums[k]
            right_arr = right_sums[n - k]
            if not left_arr or not right_arr:
                continue
            right_ptr = len(right_arr) - 1
            left_arr_sorted = sorted(left_arr)
            for sum_l in left_arr_sorted:
                remaining = target - sum_l
                low, high = 0, len(right_arr) - 1
                best = None
                while low <= high:
                    mid = (low + high) // 2
                    if right_arr[mid] == remaining:
                        best = remaining
                        break
                    elif right_arr[mid] < remaining:
                        low = mid + 1
                    else:
                        high = mid - 1
                if best is None:
                    candidates = []
                    if high >= 0:
                        candidates.append(right_arr[high])
                    if low < len(right_arr):
                        candidates.append(right_arr[low])
                    for candidate in candidates:
                        current_sum = sum_l + candidate
                        diff = abs(total - 2 * current_sum)
                        if diff < min_diff:
                            min_diff = diff
                            if min_diff == 0:
                                return 0
                else:
                    current_sum = sum_l + best
                    diff = abs(total - 2 * current_sum)
                    if diff < min_diff:
                        min_diff = diff
                        if min_diff == 0:
                            return 0
        return min_diff
