        MOD = 10**9 + 7
        if k == 0:
            return 0

        dp_prev = [[0] * (m + 1) for _ in range(k + 1)]

        for num in range(1, m + 1):
            dp_prev[1][num] = 1

        for i in range(2, n + 1):
            dp_curr = [[0] * (m + 1) for _ in range(k + 1)]
            for cost in range(1, k + 1):
                prefix_sum = 0
                for max_num in range(1, m + 1):
                    dp_curr[cost][max_num] = dp_prev[cost][max_num] * max_num % MOD
                    if cost > 1:
                        dp_curr[cost][max_num] = (dp_curr[cost][max_num] + prefix_sum) % MOD
                    prefix_sum = (prefix_sum + dp_prev[cost - 1][max_num]) % MOD
            dp_prev = dp_curr

        total = 0
        for num in range(1, m + 1):
            total = (total + dp_prev[k][num]) % MOD

