class Solution:
    def maxNumEdgesToRemove(self, n: int, edges: List[List[int]]) -> int:
        parent_alice = list(range(n + 1))
        parent_bob = list(range(n + 1))

        def find(parent, x):
            if parent[x] != x:
                parent[x] = find(parent, parent[x])
            return parent[x]

        def union(parent, x, y):
            x_root = find(parent, x)
            y_root = find(parent, y)
            if x_root == y_root:
                return False
            parent[y_root] = x_root
            return True

        edges.sort(reverse=True)
        removed = 0
        alice_components = n
        bob_components = n

        for typ, u, v in edges:
            if typ == 3:
                alice_union = union(parent_alice, u, v)
                bob_union = union(parent_bob, u, v)
                if alice_union and bob_union:
                    alice_components -= 1
                    bob_components -= 1
                elif not alice_union and not bob_union:
                    removed += 1
            elif typ == 2:
                if union(parent_bob, u, v):
                    bob_components -= 1
                else:
                    removed += 1
            elif typ == 1:
                if union(parent_alice, u, v):
                    alice_components -= 1
                else:
                    removed += 1

        if alice_components != 1 or bob_components != 1:
            return -1
        return removed
