class Solution:
    def maximumInvitations(self, favorite: List[int]) -> int:
        n = len(favorite)
        visited = [False] * n
        max_cycle = 0
        mutual_pairs = []

        for i in range(n):
            if favorite[favorite[i]] == i and i < favorite[i]:
                mutual_pairs.append((i, favorite[i]))

        graph = defaultdict(list)
        for u in range(n):
            v = favorite[u]
            if favorite[v] != u:
                graph[v].append(u)

        depth = [1] * n
        for u in range(n):
            if favorite[favorite[u]] != u:
                v = favorite[u]
                if favorite[v] != u:
                    depth[v] = max(depth[v], depth[u] + 1)

        total_pairs = 0
        for a, b in mutual_pairs:
            total_pairs += depth[a] + depth[b]

        for i in range(n):
            if not visited[i]:
                path = []
                current = i
                while True:
                    if visited[current]:
                        if current in path:
                            idx = path.index(current)
                            cycle_len = len(path) - idx
                            max_cycle = max(max_cycle, cycle_len)
                        break
                    visited[current] = True
                    path.append(current)
                    current = favorite[current]

        return max(max_cycle, total_pairs)
