class Solution:
    def numberOfSets(self, n: int, maxDistance: int, roads: List[List[int]]) -> int:
        res = 0
        adj = [[] for _ in range(n)]
        for u, v, w in roads:
            adj[u].append((v, w))
            adj[v].append((u, w))

        for mask in range(1 << n):
            active = [i for i in range(n) if (mask & (1 << i))]
            m = len(active)
            if m == 0:
                res += 1
                continue

            dist = [[float('inf')] * m for _ in range(m)]
            for i in range(m):
                dist[i][i] = 0

            node_to_idx = {node: idx for idx, node in enumerate(active)}

            for u, v, w in roads:
                if u in node_to_idx and v in node_to_idx:
                    i = node_to_idx[u]
                    j = node_to_idx[v]
                    if w < dist[i][j]:
                        dist[i][j] = w
                        dist[j][i] = w

            valid = True
            for k in range(m):
                for i in range(m):
                    if dist[i][k] == float('inf'):
                        continue
                    for j in range(m):
                        if dist[k][j] != float('inf') and dist[i][k] + dist[k][j] < dist[i][j]:
                            dist[i][j] = dist[i][k] + dist[k][j]
                if k >= 0:  # Check after each k to potentially break early
                    valid = True
                    for i in range(m):
                        for j in range(i + 1, m):
                            if dist[i][j] > maxDistance:
                                valid = False
                                break
                        if not valid:
                            break
                    if not valid:
                        break
            else:
                valid = True
                for i in range(m):
                    for j in range(i + 1, m):
                        if dist[i][j] > maxDistance:
                            valid = False
                            break
                    if not valid:
                        break

            if valid:
