from typing import List

class Solution:
    def latestDayToCross(self, row: int, col: int, cells: List[List[int]]) -> int:
        left, right = 0, len(cells) - 1
        answer = 0
        dirs = [(-1, 0), (1, 0), (0, -1), (0, 1)]

        while left <= right:
            mid = (left + right) // 2
            grid = [[0] * col for _ in range(row)]
            for i in range(mid + 1):
                r, c = cells[i]
                grid[r-1][c-1] = 1

            stack = []
            visited = [[False] * col for _ in range(row)]
            for c in range(col):
                if grid[0][c] == 0:
                    stack.append((0, c))
                    visited[0][c] = True

            found = False
            while stack:
                r, c = stack.pop()
                if r == row - 1:
                    found = True
                    break
                for dr, dc in dirs:
                    nr, nc = r + dr, c + dc
                    if 0 <= nr < row and 0 <= nc < col and grid[nr][nc] == 0 and not visited[nr][nc]:
                        visited[nr][nc] = True
                        stack.append((nr, nc))

            if found:
                answer = mid
                left = mid + 1
            else:
                right = mid - 1

        return answer + 1 if answer + 1 < len(cells) else answer
