                if d_squared > 4 * r_squared + 1e-8:
                    continue
                d = math.sqrt(d_squared)
                mid_x, mid_y = (x1 + x2) / 2, (y1 + y2) / 2
                if d_squared == 0:
                    h = 0
                else:
                    h = math.sqrt(r_squared - d_squared / 4)
                for sign in (-1, 1):
                    cx = mid_x - sign * h * dy / d if d != 0 else mid_x
                    cy = mid_y + sign * h * dx / d if d != 0 else mid_y
                    count = 0
                    for (x, y) in darts:
                        if (x - cx)**2 + (y - cy)**2 <= r_squared + 1e-8:
                            count += 1
                    if count > max_points:
                        max_points = count
        return max_points
