from collections import deque

class Solution:
    def minimumOperations(self, root: Optional[TreeNode]) -> int:
        if not root:
            return 0

        queue = deque([root])
        operations = 0

        while queue:
            level_size = len(queue)
            current_level = []
            for _ in range(level_size):
                node = queue.popleft()
                current_level.append(node.val)
                if node.left:
                    queue.append(node.left)
                if node.right:
                    queue.append(node.right)

            sorted_level = sorted(current_level)
            if current_level == sorted_level:
                continue

            indexed_values = [(val, idx) for idx, val in enumerate(current_level)]
            indexed_values.sort()

            visited = [False] * len(current_level)

            for i in range(len(current_level)):
                if visited[i] or indexed_values[i][1] == i:
                    continue

                cycle_size = 0
                j = i
                while not visited[j]:
                    visited[j] = True
                    j = indexed_values[j][1]
                    cycle_size += 1

                operations += (cycle_size - 1)

        return operations
