            for i in range(n, -1, -1):
                for j in range(k, -1, -1):
                    if dp[i][j]:
                        if i + 1 <= n and j + num <= k:
                            dp[i + 1][j + num] = (dp[i + 1][j + num] + dp[i][j]) % MOD

