        prime_counts = defaultdict(int)

        for i in range(m):
            for j in range(n):
                for di, dj in directions:
                    num = 0
                    ni, nj = i, j
                    while 0 <= ni < m and 0 <= nj < n:
                        num = num * 10 + mat[ni][nj]
                        if num > 10:
