class Solution:
    def countPalindromes(self, s: str) -> int:
        MOD = 10**9 + 7
        n = len(s)
        if n < 5:
            return 0

        prefix = [0] * 10
        prefix_counts = [prefix.copy()]
        for c in s:
            new_prefix = prefix.copy()
            new_prefix[int(c)] += 1
            prefix_counts.append(new_prefix)
            prefix = new_prefix

        suffix = [0] * 10
        suffix_counts = [suffix.copy()]
        for c in reversed(s):
            new_suffix = suffix.copy()
            new_suffix[int(c)] += 1
            suffix_counts.append(new_suffix)
            suffix = new_suffix
        suffix_counts.reverse()

        prefix_2 = [[0] * 10 for _ in range(10)]
        prefix_2_counts = [ [row.copy() for row in prefix_2] ]
        for i in range(1, n+1):
            current_d = int(s[i-1])
            new_prefix_2 = [row.copy() for row in prefix_2_counts[-1]]
            for d in range(10):
                new_prefix_2[d][current_d] += prefix_counts[i-1][d]
            prefix_2_counts.append(new_prefix_2)

        suffix_2 = [[0] * 10 for _ in range(10)]
        suffix_2_counts = [ [row.copy() for row in suffix_2] for _ in range(n+1) ]
        for i in range(n-1, -1, -1):
            current_d = int(s[i])
            for d in range(10):
                suffix_2_counts[i][current_d][d] += suffix_counts[i+1][d]
            if i < n-1:
                for d1 in range(10):
                    for d2 in range(10):
                        suffix_2_counts[i][d1][d2] += suffix_2_counts[i+1][d1][d2]

        res = 0
        for i in range(2, n-2):
            for d1 in range(10):
                for d2 in range(10):
                    cnt_prefix = prefix_2_counts[i][d1][d2]
                    cnt_suffix = suffix_2_counts[i+1][d2][d1]
                    res = (res + cnt_prefix * cnt_suffix) % MOD
        return res
