        def can_partition(s, target, memo):
            if (s, target) in memo:
                return memo[(s, target)]
            if not s and target == 0:
                memo[(s, target)] = True
                return True
            if target < 0:
                memo[(s, target)] = False
                return False
            for i in range(1, len(s)+1):
                num = int(s[:i])
                if can_partition(s[i:], target - num, memo):
                    memo[(s, target)] = True
                    return True
            memo[(s, target)] = False
            return False

        total = 0
        for i in range(1, n+1):
            square = i * i
            if can_partition(str(square), i, {}):
                total += square
        return total
