        dp = [1] * n
        prev = [-1] * n
        len_map = {}

        for i in range(n):
            length = len(words[i])
            if length not in len_map:
                len_map[length] = []
            len_map[length].append(i)

        for i in range(n):
            current_len = len(words[i])
            if current_len not in len_map:
                continue
            for j in len_map[current_len]:
                if j >= i:
                    continue
                if groups[i] != groups[j]:
                    hamming = sum(1 for a, b in zip(words[i], words[j]) if a != b)
                    if hamming == 1 and dp[j] + 1 > dp[i]:
                        dp[i] = dp[j] + 1
                        prev[i] = j

        max_len = max(dp) if dp else 0
        if max_len == 0:
            return []
        if max_len == 1:
            return [words[dp.index(1)]]

        idx = dp.index(max_len)
        res = []
        while idx != -1:
            res.append(words[idx])
            idx = prev[idx]

        return res[::-1]
