        cols = len(grid[0]) if rows > 0 else 0

        cat_pos = None
        mouse_pos = None
        food_pos = None
        for i in range(rows):
            for j in range(cols):
                if grid[i][j] == 'C':
                    cat_pos = (i, j)
                elif grid[i][j] == 'M':
                    mouse_pos = (i, j)
                elif grid[i][j] == 'F':
                    food_pos = (i, j)

        directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]

        @lru_cache(maxsize=None)
        def dfs(turn, cat, mouse):
            if turn > 100:
                return False
            if cat == mouse:
                return False
            if cat == food_pos:
                return False
            if mouse == food_pos:
                return True

            if turn % 2 == 0:  # Mouse's turn
                i, j = mouse
                for di, dj in directions:
                    for step in range(1, mouseJump + 1):
                        ni, nj = i + di * step, j + dj * step
                        if 0 <= ni < rows and 0 <= nj < cols and grid[ni][nj] != '#':
                            if (ni, nj) == food_pos:
                                return True
                        else:
                            break
                for di, dj in directions:
                    for step in range(1, mouseJump + 1):
                        ni, nj = i + di * step, j + dj * step
                        if 0 <= ni < rows and 0 <= nj < cols and grid[ni][nj] != '#':
                            if dfs(turn + 1, cat, (ni, nj)):
                                return True
                        else:
                            break
                if dfs(turn + 1, cat, mouse):
                    return True
                return False
            else:  # Cat's turn
                i, j = cat
                for di, dj in directions:
                    for step in range(1, catJump + 1):
                        ni, nj = i + di * step, j + dj * step
                        if 0 <= ni < rows and 0 <= nj < cols and grid[ni][nj] != '#':
                            if (ni, nj) == mouse or (ni, nj) == food_pos:
                                return False
                        else:
                            break
                for di, dj in directions:
                    for step in range(1, catJump + 1):
                        ni, nj = i + di * step, j + dj * step
                        if 0 <= ni < rows and 0 <= nj < cols and grid[ni][nj] != '#':
                            if not dfs(turn + 1, (ni, nj), mouse):
                                return False
                        else:
                            break
                if not dfs(turn + 1, cat, mouse):
                    return False
                return True

