from typing import List

class Solution:
    def minimumTime(self, grid: List[List[int]]) -> int:
        if grid[0][1] > 1 and grid[1][0] > 1:
            return -1

        m, n = len(grid), len(grid[0])
        directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
        heap = []
        heapq.heappush(heap, (0, 0, 0))  # (time, row, col)
        visited = [[float('inf')] * n for _ in range(m)]
        visited[0][0] = 0

        while heap:
            time, row, col = heapq.heappop(heap)
            if row == m - 1 and col == n - 1:
                return time
            if time > visited[row][col]:
                continue
            for dr, dc in directions:
                nr, nc = row + dr, col + dc
                if 0 <= nr < m and 0 <= nc < n:
                    new_time = time + 1
                    if grid[nr][nc] > new_time:
                        diff = grid[nr][nc] - time
                        new_time = grid[nr][nc] + (0 if diff % 2 == 1 else 1)
                    if new_time < visited[nr][nc]:
                        visited[nr][nc] = new_time
                        heapq.heappush(heap, (new_time, nr, nc))
        return -1
