        MOD = 10**9 + 7

        def get_valid_states(m):
            from itertools import product
            colors = [0, 1, 2]
            states = []
            for state in product(colors, repeat=m):
                valid = True
                for i in range(m - 1):
                    if state[i] == state[i + 1]:
                        valid = False
                        break
                if valid:
                    states.append(state)
