        for i in range(k):
            for j in range(k):
                valid = True
                for x in range(m):
                    if states[i][x] == states[j][x]:
                        valid = False
                        break
                if valid:
                    transition[i].append(j)

        dp = [1] * k

        for _ in range(1, n):
            new_dp = [0] * k
            for i in range(k):
                for j in transition[i]:
                    new_dp[j] = (new_dp[j] + dp[i]) % MOD
            dp = new_dp

