class Solution:
    def canMerge(self, trees: List[TreeNode]) -> Optional[TreeNode]:
        if not trees:
            return None

        leaves = set()
        root_dict = {}

        for tree in trees:
            root_dict[tree.val] = tree
            if tree.left:
                leaves.add(tree.left.val)
            if tree.right:
                leaves.add(tree.right.val)

        candidates = []
        for tree in trees:
            if tree.val not in leaves:
                candidates.append(tree)

        if len(candidates) != 1:
            return None

        root = candidates[0]
        del root_dict[root.val]
        stack = [(root, float('-inf'), float('inf'))]
        count = 1

        while stack:
            node, lower, upper = stack.pop()
            if node.val <= lower or node.val >= upper:
                return None
            if node.left:
                if node.left.val in root_dict:
                    left_node = root_dict.pop(node.left.val)
                    node.left = left_node
                    count += 1
                stack.append((node.left, lower, node.val))
            if node.right:
                if node.right.val in root_dict:
                    right_node = root_dict.pop(node.right.val)
                    node.right = right_node
                    count += 1
                stack.append((node.right, node.val, upper))

        return root if not root_dict and count == len(trees) else None
