        parent = list(range(n))

        def find(u):
            while parent[u] != u:
                parent[u] = parent[parent[u]]
                u = parent[u]
            return u

        res = []
        for u, v in requests:
            u_root = find(u)
            v_root = find(v)
            if u_root == v_root:
                res.append(True)
                continue

            valid = True
            for x, y in restrictions:
                x_root = find(x)
                y_root = find(y)
                if (x_root == u_root and y_root == v_root) or (x_root == v_root and y_root == u_root):
                    valid = False
                    break
            if valid:
                parent[v_root] = u_root
                res.append(True)
            else:
                res.append(False)
