class Solution:
    def specialPerm(self, nums: List[int]) -> int:
        from functools import lru_cache

        n = len(nums)
        MOD = 10**9 + 7

        @lru_cache(maxsize=None)
        def dfs(prev, mask):
            if mask == (1 << n) - 1:
                return 1
            total = 0
            for i in range(n):
                if not (mask & (1 << i)) and (prev % nums[i] == 0 or nums[i] % prev == 0):
                    total += dfs(nums[i], mask | (1 << i))
                    if total >= MOD:
                        total -= MOD
            return total

        result = 0
        for i in range(n):
            result += dfs(nums[i], 1 << i)
            if result >= MOD:
                result -= MOD
        return result
