        nums_sorted = sorted(nums)
        n = len(nums_sorted)
        for i in range(n):
            x = nums_sorted[i]
            min_y = x // 2  # Since |x - y| <= min(x, y) => y >= x/2
            left = i
            right = n - 1
            while left <= right:
                mid = (left + right) // 2
                if nums_sorted[mid] >= min_y:
                    right = mid - 1
                else:
                    left = mid + 1
            for j in range(left, n):
