        n = len(nums)
        if n == 1:
            return 1

        left = 0
        while left + 1 < n and nums[left] < nums[left + 1]:
            left += 1

        if left == n - 1:
            return n * (n + 1) // 2

        right = n - 1
        while right > 0 and nums[right - 1] < nums[right]:
            right -= 1

