            else:
                if (points, student_id) > heap[0]:
                    heapq.heappop(heap)
                    heapq.heappush(heap, (points, student_id))

        top_students = []
        while heap:
            points, student_id = heapq.heappop(heap)
            top_students.append((points, student_id))
        top_students.sort(reverse=True)

        result = []
