from collections import deque

class Solution:
    def buildMatrix(self, k: int, rowConditions: List[List[int]], colConditions: List[List[int]]) -> List[List[int]]:
        def topological_sort(conditions, k):
            graph = [[] for _ in range(k+1)]
            in_degree = [0] * (k+1)

            edge_set = set()
            for u, v in conditions:
                if (u, v) not in edge_set:
                    edge_set.add((u, v))
                    graph[u].append(v)
                    in_degree[v] += 1

            queue = deque()
            for i in range(1, k+1):
                if in_degree[i] == 0:
                    queue.append(i)

            order = []
            while queue:
                u = queue.popleft()
                order.append(u)
                for v in graph[u]:
                    in_degree[v] -= 1
                    if in_degree[v] == 0:
                        queue.append(v)

            return order if len(order) == k else None

        row_order = topological_sort(rowConditions, k)
        col_order = topological_sort(colConditions, k)

        if not row_order or not col_order:
            return []

        row_pos = [0] * (k + 1)
        col_pos = [0] * (k + 1)
        for i, num in enumerate(row_order):
            row_pos[num] = i
        for i, num in enumerate(col_order):
            col_pos[num] = i

        matrix = [[0] * k for _ in range(k)]
        for num in range(1, k+1):
            matrix[row_pos[num]][col_pos[num]] = num

        return matrix
