        if m == 0:
            return True
        n = len(targetGrid[0])

        color_rect = {}

        for i in range(m):
            for j in range(n):
                color = targetGrid[i][j]
                if color not in color_rect:
                    color_rect[color] = [i, i, j, j]  # min_row, max_row, min_col, max_col
                else:
                    rect = color_rect[color]
                    if i < rect[0]:
                        rect[0] = i
                    elif i > rect[1]:
                        rect[1] = i
                    if j < rect[2]:
                        rect[2] = j
                    elif j > rect[3]:
                        rect[3] = j

        graph = {}
        colors = color_rect.keys()

        for color in colors:
            graph[color] = set()

        for color in colors:
            min_row, max_row, min_col, max_col = color_rect[color]
            other_colors = set()
            for i in range(min_row, max_row + 1):
                for j in range(min_col, max_col + 1):
                    other_color = targetGrid[i][j]
                    if other_color != color:
                        other_colors.add(other_color)
            for other_color in other_colors:
                graph[other_color].add(color)

        visited = {}
        recursion_stack = {}

        def has_cycle(node):
            visited[node] = True
            recursion_stack[node] = True
            for neighbor in graph[node]:
                if neighbor not in visited:
                    if has_cycle(neighbor):
                        return True
                elif recursion_stack[neighbor]:
                    return True
            recursion_stack[node] = False
            return False

        for node in graph:
            if node not in visited:
                if has_cycle(node):
                    return False
        return True
