class Solution:
    def replaceValueInTree(self, root: Optional[TreeNode]) -> Optional[TreeNode]:
        if not root:
            return None

        root.val = 0
        queue = deque([root])

        while queue:
            level_size = len(queue)
            level_sum = 0
            parent_to_children_sum = {}
            nodes = []

            for _ in range(level_size):
                node = queue.popleft()
                nodes.append(node)
                children_sum = 0
                if node.left:
                    children_sum += node.left.val
                    queue.append(node.left)
                if node.right:
                    children_sum += node.right.val
                    queue.append(node.right)
                parent_to_children_sum[node] = children_sum
                level_sum += children_sum

            for node in nodes:
                total = level_sum - parent_to_children_sum[node]
                if node.left:
                    node.left.val = total
                if node.right:
                    node.right.val = total

        return root
