        n = len(cars)
        res = [-1.0] * n
        stack = []

        for i in range(n-1, -1, -1):
            position, speed = cars[i]
            while stack:
                j = stack[-1]
                next_pos, next_speed = cars[j]
                if speed <= next_speed:
                    stack.pop()
                else:
                    collision_time = (next_pos - position) / (speed - next_speed)
                    if res[j] > 0 and collision_time >= res[j]:
