class Solution:
    def minDistance(self, houses: List[int], k: int) -> int:
        houses.sort()
        n = len(houses)

        cost = [[0] * n for _ in range(n)]
        for i in range(n):
            for j in range(i, n):
                median_pos = (i + j) // 2
                median = houses[median_pos]
                left_sum = median * (median_pos - i) - (houses[i] + houses[median_pos - 1]) * (median_pos - i) // 2 if median_pos > i else 0
                right_sum = (houses[median_pos + 1] + houses[j]) * (j - median_pos) // 2 - median * (j - median_pos) if j > median_pos else 0
                total = left_sum + right_sum + (houses[median_pos] - median) * (median_pos >= i and median_pos <= j)
                cost[i][j] = total

        dp = [[float('inf')] * k for _ in range(n)]
        for i in range(n):
            dp[i][0] = cost[0][i]

        for l in range(1, k):
            for i in range(n):
                for j in range(i):
                    if dp[j][l-1] + cost[j+1][i] < dp[i][l]:
                        dp[i][l] = dp[j][l-1] + cost[j+1][i]

        return dp[n-1][k-1]
