            graph = defaultdict(list)
            current_meetings = time_map[time]
            participants = set()

            for x, y in current_meetings:
                graph[x].append(y)
                graph[y].append(x)
                participants.add(x)
                participants.add(y)

            queue = deque()
            new_known = set()
            for person in participants:
                if person in known:
                    queue.append(person)
                    new_known.add(person)

            while queue:
                current = queue.popleft()
                for neighbor in graph[current]:
                    if neighbor not in new_known:
                        new_known.add(neighbor)
                        queue.append(neighbor)

            known.update(new_known)

        return list(known)
