import math
from collections import defaultdict
from math import gcd
from itertools import combinations

class Solution:
    def gcdSort(self, nums: List[int]) -> bool:
        unique_nums = list(set(nums))
        max_num = max(unique_nums) if unique_nums else 0
        sieve = list(range(max_num + 1))
        for i in range(2, int(math.sqrt(max_num)) + 1):
            if sieve[i] == i:
                for j in range(i*i, max_num + 1, i):
                    if sieve[j] == j:
                        sieve[j] = i

        parent = {}
        rank = defaultdict(int)

        def find(u):
            while parent[u] != u:
                parent[u] = parent[parent[u]]
                u = parent[u]
            return u

        def union(u, v):
            u_root = find(u)
            v_root = find(v)
            if u_root == v_root:
                return
            if rank[u_root] > rank[v_root]:
                parent[v_root] = u_root
            else:
                parent[u_root] = v_root
                if rank[u_root] == rank[v_root]:
                    rank[v_root] += 1

        num_to_primes = {}
        for num in unique_nums:
            if num == 1:
                continue
            x = num
            primes = set()
            while x > 1:
                p = sieve[x]
                primes.add(p)
                while x % p == 0:
                    x = x // p
            num_to_primes[num] = primes

        for num, primes in num_to_primes.items():
            if not primes:
                continue
            primes = sorted(primes)
            parent.setdefault(primes[0], primes[0])
            for p in primes[1:]:
                parent.setdefault(p, p)
                union(primes[0], p)

        sorted_nums = sorted(nums)
        for a, b in zip(nums, sorted_nums):
            if a == b:
                continue
            if a == 1 or b == 1:
                return False
            a_primes = num_to_primes.get(a, set())
            b_primes = num_to_primes.get(b, set())
            if not a_primes or not b_primes:
                return False
            a_root = find(next(iter(a_primes)))
            b_root = find(next(iter(b_primes)))
            if a_root != b_root:
                return False
        return True
