        def merge(s1, s2):
            if s2 in s1:
                return s1
            max_overlap = min(len(s1), len(s2))
            while max_overlap > 0:
                if s1.endswith(s2[:max_overlap]):
                    return s1 + s2[max_overlap:]
                max_overlap -= 1
            return s1 + s2

