                root = candidate
                valid_root = True
                break
        if not valid_root:
            return 0

        res = 1
        nodes_sorted = sorted(nodes, key=lambda x: len(adj[x]), reverse=True)
        node_to_index = {node: idx for idx, node in enumerate(nodes_sorted)}

        for node in nodes_sorted:
            if node == root:
                continue
            parent = -1
            min_parent_index = float('inf')
            current_degree = len(adj[node])

            for neighbor in adj[node]:
                neighbor_degree = len(adj[neighbor])
                if neighbor_degree >= current_degree and node_to_index[neighbor] < min_parent_index:
