        m, n = len(grid), len(grid[0])
        MOD = 10**9 + 7

        dp_prev = [{} for _ in range(n)]
        dp_prev[0][grid[0][0] % k] = 1

        for i in range(m):
            dp_current = [{} for _ in range(n)]
            for j in range(n):
                current_val = grid[i][j]
                if i == 0 and j == 0:
                    dp_current[j][grid[i][j] % k] = 1
                    continue
                combined = {}
                if i > 0:
                    for rem, count in dp_prev[j].items():
                        new_rem = (rem + current_val) % k
                        combined[new_rem] = (combined.get(new_rem, 0) + count) % MOD
                if j > 0:
                    for rem, count in dp_current[j-1].items():
                        new_rem = (rem + current_val) % k
                        combined[new_rem] = (combined.get(new_rem, 0) + count) % MOD
                dp_current[j] = combined
