        graph = [set() for _ in range(n + 1)]
        degree = [0] * (n + 1)
        edge_exists = [[False] * (n + 1) for _ in range(n + 1)]

        for u, v in edges:
            graph[u].add(v)
            graph[v].add(u)
            degree[u] += 1
            degree[v] += 1
            edge_exists[u][v] = True
            edge_exists[v][u] = True

        min_degree = float('inf')

        for u in range(1, n + 1):
            neighbors = sorted(graph[u])
            len_neighbors = len(neighbors)
            for i in range(len_neighbors):
                v = neighbors[i]
                if v <= u:
                    continue
                for j in range(i + 1, len_neighbors):
                    w = neighbors[j]
                    if w <= v:
                        continue
                    if edge_exists[v][w]:
                        trio_degree = degree[u] + degree[v] + degree[w] - 6
                        if trio_degree < min_degree:
                            min_degree = trio_degree
                            if min_degree == 0:
