class Solution:
    def maxGeneticDifference(self, parents: List[int], queries: List[List[int]]) -> List[int]:
        from collections import defaultdict

        tree = defaultdict(list)
        root = -1
        for i, p in enumerate(parents):
            if p == -1:
                root = i
            else:
                tree[p].append(i)

        query_map = defaultdict(list)
        for idx, (node, val) in enumerate(queries):
            query_map[node].append((val, idx))

        ans = [0] * len(queries)

        class TrieNode:
            __slots__ = ['children', 'count']
            def __init__(self):
                self.children = [None, None]
                self.count = 0

        root_trie = TrieNode()

        def insert(num):
            node = root_trie
            for i in range(17, -1, -1):
                bit = (num >> i) & 1
                if not node.children[bit]:
                    node.children[bit] = TrieNode()
                node = node.children[bit]
                node.count += 1

        def remove(num):
            node = root_trie
            for i in range(17, -1, -1):
                bit = (num >> i) & 1
                node = node.children[bit]
                node.count -= 1

        def query(num):
            res = 0
            node = root_trie
            for i in range(17, -1, -1):
                bit = (num >> i) & 1
                toggled_bit = 1 - bit
                if node.children[toggled_bit] and node.children[toggled_bit].count > 0:
                    res |= (1 << i)
                    node = node.children[toggled_bit]
                else:
                    node = node.children[bit]
            return res

        stack = [(root, True)]

        while stack:
            node, is_entering = stack.pop()
            if is_entering:
                insert(node)
                stack.append((node, False))
                for child in reversed(tree[node]):
                    stack.append((child, True))
            else:
                if node in query_map:
                    for val, idx in query_map[node]:
                        ans[idx] = query(val)
                remove(node)

        return ans
