        for x, y, l in squares:
            top = y + l
            events.append((y, 1, x, x + l))
            events.append((top, -1, x, x + l))

        events.sort()

        active_intervals = []
        total_area = 0
        prev_y = None

        for y, typ, x1, x2 in events:
            if prev_y is not None and y != prev_y and active_intervals:
                delta_y = y - prev_y
                active_intervals.sort()
                merged = []
                for interval in active_intervals:
                    if not merged:
                        merged.append(interval)
                    else:
                        last_x1, last_x2 = merged[-1]
                        if interval[0] <= last_x2:
                            merged[-1] = (last_x1, max(last_x2, interval[1]))
                        else:
                            merged.append(interval)
                current_width = 0
                for (start, end) in merged:
                    current_width += end - start
                total_area += current_width * delta_y

            if typ == 1:
                bisect.insort(active_intervals, (x1, x2))
            else:
                idx = bisect.bisect_left(active_intervals, (x1, x2))
                if idx < len(active_intervals) and active_intervals[idx] == (x1, x2):
                    active_intervals.pop(idx)

            prev_y = y

        target = total_area / 2

        events = []
        for x, y, l in squares:
            top = y + l
            events.append((y, 1, x, x + l))
            events.append((top, -1, x, x + l))

        events.sort()

