    def minOperations(self, nums: List[int], target: int) -> int:
        total = sum(nums)
        if total < target:
            return -1

        freq = [0] * 32
        for num in nums:
            power = num.bit_length() - 1
            freq[power] += 1

        operations = 0
        for i in range(31):
            bit_needed = (target >> i) & 1
            if bit_needed:
