        count = [0] * 31
        for num in nums:
            count[num] += 1

        mask = [0] * 31
        for num in range(2, 31):
            tmp = num
            m = 0
            for i, p in enumerate(primes):
                if tmp % p == 0:
                    tmp //= p
                    m |= (1 << i)
                    if tmp % p == 0:
                        mask[num] = -1
                        break
            if mask[num] != -1:
                mask[num] = m

        dp = [0] * (1 << len(primes))
        dp[0] = 1

        for num in range(2, 31):
            if mask[num] == -1 or count[num] == 0:
                continue
            current_mask = mask[num]
            cnt = count[num]
            masks_to_update = []
            for prev_mask in range(len(dp)):
                if (prev_mask & current_mask) == 0:
                    new_mask = prev_mask | current_mask
                    masks_to_update.append((new_mask, prev_mask))
            for new_mask, prev_mask in masks_to_update:
                dp[new_mask] = (dp[new_mask] + dp[prev_mask] * cnt) % MOD

        total = 0
        for m in range(1, len(dp)):
            total = (total + dp[m]) % MOD

        if count[1] > 0:
            total = (total * pow(2, count[1], MOD)) % MOD

        return total
