                num_bit = (num >> i) & 1
                limit_bit = (limit >> i) & 1
                if limit_bit:
                    if node.children[num_bit]:
                        res += node.children[num_bit].count
                    node = node.children[1 - num_bit]
                else:
                    node = node.children[num_bit]
            return res

        res = 0
        for num in nums:
            res += query(num, high + 1) - query(num, low)
            insert(num)
