class Solution:
    def smallestSubsequence(self, s: str, k: int, letter: str, repetition: int) -> str:
        stack = []
        n = len(s)
        total_letter = s.count(letter)
        remaining_letter = total_letter
        needed = repetition

        for i, ch in enumerate(s):
            while stack and stack[-1] > ch and (len(stack) + (n - i)) > k:
                if stack[-1] == letter:
                    if remaining_letter <= needed:
                        break
                    needed += 1
                stack.pop()

            if len(stack) < k:
                if ch == letter:
                    stack.append(ch)
                    needed -= 1
                else:
                    remaining_positions = k - len(stack)
                    if remaining_positions > needed:
                        stack.append(ch)
            if ch == letter:
                remaining_letter -= 1

