                graph[o] = {}
            if c not in graph[o] or graph[o][c] > co:
                graph[o][c] = co
            nodes.add(o)
            nodes.add(c)

        dist = {}
        for node in nodes:
            dist[node] = {node: 0}
            heap = [(0, node)]
            while heap:
                current_dist, u = heapq.heappop(heap)
                if current_dist > dist[node].get(u, float('inf')):
                    continue
                if u in graph:
                    for v in graph[u]:
                        new_dist = current_dist + graph[u][v]
                        if v not in dist[node] or new_dist < dist[node][v]:
                            dist[node][v] = new_dist
                            heapq.heappush(heap, (new_dist, v))

        total_cost = 0
        for s_char, t_char in zip(source, target):
            if s_char == t_char:
                continue
            if s_char not in dist or t_char not in dist[s_char]:
                return -1
            total_cost += dist[s_char][t_char]

        return total_cost
